import { RECEIVE_QUESTIONS, ADD_ANSWER_TO_QUESTION } from '../actions/questions';

export default function questions(state = {}, action) {
    switch (action.type) {
        case RECEIVE_QUESTIONS:
            return {
                ...state,
                ...action.questions
            };
        default:
            return state;
    }
}
