import { RECEIVE_USERS, ADD_ANSWER_TO_USER } from '../actions/users';

export default function users(state = {}, action) {
    switch (action.type) {
        case RECEIVE_USERS:
            return {
                ...state,
                ...action.users
            };
        default:
            return state;
    }
}
